import axios from "axios";
const TodosList = ({ todos, setTodos, setEditTodo,fetchData, setEditId }) => {


  const deleteTask = (id) => {
    axios.delete("http://localhost:8000/todos/" + id)
      .then(function(response) {
        fetchData();
        console.log(response)
      }).catch(function(error) {
        console.log(error)
      })
  }

  const editHandler = (id) => {
    setTodos(
      todos.map((todo) =>
        todo.id === id ? { ...todo, isEditing: !todo.isEditing } : todo
      )
    );
    const findTodo = todos.find((todo) => todo.id === id);
    setEditTodo(findTodo);
    setEditId(findTodo.id);
  };

  const completedHandler = (todo) => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return { ...item, completed: !item.completed };
        }
        return item;
      })
    );
  };

  return (
    <div>
      {todos.map((todo) => !todo.edited ?
         (
          <div>
            <table>

            <tr className="list-item" key={todo.id}>
              <td
                className={`list ${todo.completed ? "complete" : "" }`}
                onChange={(e) => e.preventDefault()}
              >{todo.title}</td>

              <td
                className={`list ${todo.completed ? "complete" : "" }`}
                onChange={(e) => e.preventDefault()}
              >{todo.owner}</td>

              <td
                className={`list ${todo.completed ? "complete" : "" }`}
                onChange={(e) => e.preventDefault()}
              >{todo.location}</td>

              <button
                className="button-complete task-button"
                onClick={() => completedHandler(todo)}
              >
                <span>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-check-circle"
                    viewBox="0 0 16 16"
                  >
                    <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                    <path d="m10.97 4.97-.02.022-3.473 4.425-2.093-2.094a.75.75 0 0 0-1.06 1.06L6.97 11.03a.75.75 0 0 0 1.079-.02l3.992-4.99a.75.75 0 0 0-1.071-1.05" />
                  </svg>
                </span>
              </button>

              <button
                className="button-edit task-button"
                onClick={(e) => editHandler(todo.id)}
              >
                <span>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-pencil"
                    viewBox="0 0 16 16"
                  >
                    <path d="M12.146.146a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1 0 .708l-10 10a.5.5 0 0 1-.168.11l-5 2a.5.5 0 0 1-.65-.65l2-5a.5.5 0 0 1 .11-.168zM11.207 2.5 13.5 4.793 14.793 3.5 12.5 1.207zm1.586 3L10.5 3.207 4 9.707V10h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.293zm-9.761 5.175-.106.106-1.528 3.821 3.821-1.528.106-.106A.5.5 0 0 1 5 12.5V12h-.5a.5.5 0 0 1-.5-.5V11h-.5a.5.5 0 0 1-.468-.325" />
                  </svg>
                </span>
              </button>

              <button
                className="button-delete task-button"
                onClick={(e) => deleteTask(todo.id)}
              >
                <span>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-x-circle"
                    viewBox="0 0 16 16"
                  >
                    <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                    <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708" />
                  </svg>
                </span>
              </button>
            </tr>
            </table>
          </div>
        ) : (    
        <form onSubmit={onFormSubmit}>
          <input
            type="text"
            placeholder="add a new task..."
            className="task-input"
            value={inputTitle}
            required
            onChange={titleChange}
          />
          <input
            type="text"
            placeholder="add an owner..."
            className="task-input"
            value={inputOwner}
            required
            onChange={ownerChange}
          />

           <input
            type="text"
            placeholder="add a location..."
            className="task-input"
            value={inputLocation}
            required
            onChange={locationChange}
          />

          <button type="submit" className="button-add">
            add
          </button>
        </form>)
      )}
    </div>
  );
};

export default TodosList;
