import { useEffect, useState } from "react";
import "./App.css";
import Form from "./Components/Form";
import Header from "./Components/Header";
import axios from "axios";
import Map from "./Components/Map";

function App() {

  const [todos, setTodos] = useState([]);
  const [editTodo, setEditTodo] = useState(null);
  const [editId, setEditId] = useState("");

  const [editedTodo, setEditedTodo] = useState({})

  const fetchData = () => {
    axios.get("http://localhost:8000/todos").then((response) => {
      setTodos(response.data);
      return response;
    });
  };

  useEffect(() => {
    fetchData();
  }, []);

    // delete
    const updateDelete = (id)=> {
      deleteTask(id)
     }

    const deleteTask = (id) => {
      axios
        .delete("http://localhost:8000/todos/" + id)
        .then(function (response) {
          fetchData();
          console.log(response);
        })
        .catch(function (error) {
          console.log(error);
        });
    };

// edit
const editHandler = (data) => {

  setTodos(
    todos.map((todo)=>
      todo.id === data.id ? {...todo, edited: !todo.edited} :todo
    )
  )


  setEditTodo(data);
  setEditedTodo({title:data.title, owner:data.owner, location:data.location})
  console.log(editedTodo)
  setEditId(data.id);
};

//complete
const completedHandler = (todo) => {
  setTodos(
    todos.map((item) => {
      if (item.id === todo.id) {
        return { ...item, completed: !item.completed };
      }
      completedTask(item.id)
      return item;
    })
  );
};

const completedTask = (id) => {
  axios
    .put("http://localhost:8000/todos/" + id, {
      id: id,
      title: todos.title,
      owner: todos.owner,
      location: todos.locatiom,
      completed: true,
    })
    .then(function (response) {
      fetchData();
      console.log(response);
    })
    .catch(function (error) {
      console.log(error);
    });
};

  return (
    <div className="container">
      <div className="app-wrapper">

         <div>
          <Header />
         </div>

         <div>
          <Form fetchData={fetchData} />
         </div>

         <div>
      <table>
        <tbody>

      {todos.map((todos) => {
          return <Map fetchData={fetchData} editTodo={editTodo} setTodos={setTodos} completedHandler={completedHandler} editHandler={editHandler} editId={editId} todos={todos} updateDelete={updateDelete}/>
      }
)}

        </tbody>
      </table>
         </div>

      </div>
    </div>
  );
}

export default App;
