  // });
  // };

  const deleteTask = (id) => {
    axios.delete("http://localhost:8000/todos/" + id)
      .then(function(response) {
        fetchData();
        console.log(response)
      }).catch(function(error) {
        console.log(error)
      })
  }

  // const editTask = (id) => {
  //   axios.put("http://localhost:8000/todos/" + id , {
