import { useEffect, useState } from "react";
import "./App.css";
import Form from "./Components/Form";
import Header from "./Components/Header";
import axios from "axios";
import TodosList from "./Components/TodosList";
import { v4 as uuidv4 } from 'uuid';

function App() {
  const [input, setInput] = useState("");
  const [todos, setTodos] = useState([]);
  const [editTodo, setEditTodo] = useState(null);

  const fetchData =()=> {axios
  .get("http://localhost:8000/todos") 
  .then((response) => {
    setTodos(response.data);
    return response});
  }


  useEffect(() => {
    fetchData()
  }, []);

  const addNewTask = () => {
    axios.post('http://localhost:8000/todos' , {
    id: uuidv4(),
    title: input,
    completed: false
  })
  .then(function (response) {
    fetchData()
    console.log(response);
  })
  .catch(function (error) {
    console.log(error);
  });
  };

  // const deleteTask = (id) => {
  //   axios.delete(`http://localhost:8000/todos/delete/${id}`)
  // .then(function (response) {
  //   fetchData()
  //   console.log(response);
  // })
  // .catch(function (error) {
  //   console.log(error);
  // });
  // };

  const deleteTask = (id) => {
    axios.delete("http://localhost:8000/todos/" + id)
      .then(function(response) {
        fetchData();
        console.log(response)
      }).catch(function(error) {
        console.log(error)
      })
  }

  const updateTodo = (title, id, completed) => {
        const copyList = [...todos];
        const itemToEdit = copyList.find(item => item.id === id);
        itemToEdit.title = input;
        setTodos(copyList);
        setEditTodo("");
    }

  const editTask = (id) => {
    axios.put("http://localhost:8000/todos/" + id , {
      id:id,
      title:"edited",
      completed: false
    })
      .then(function() {
        fetchData()
      }).catch(function(error) {
        console.log(error)
      })
  }

//   const editTask =()=> axios.put('http://localhost:8000/todos', null, {
//     id: todos.id,
//     title: input,
//     completed: false
// })
// .then(response => {
//   fetchData()
//   console.log(response);})
// .catch(error => {
//   console.log(error);
// });

  // const editTask = (id) => {
  //   axios.put(`http://localhost:8000/todos` , {
  //   id: todos.id,
  //   title: input,
  //   completed: false
  // })
  // .then(function (response) {
  //   fetchData()
  //   console.log(response);})

  // .catch(function (error) {
  //   console.log(error);
  // });
  // };


  return (
    <div className="container">

      <div className="app-wrapper">
        <div>
          <Header />
        </div>

        <div>
          <Form
            input={input}
            setInput={setInput}
            todos={todos}
            setTodos={setTodos}
            editTodo={editTodo}
            setEditTodo={setEditTodo}
            addNewTask={addNewTask}
            editTask={editTask}
            deleteTask={deleteTask}
            updateTodo={updateTodo}
          />
        </div>

        <div>
          <TodosList
            deleteTask={deleteTask}
            todos={todos}
            setTodos={setTodos}
            editTask={editTask}
            setEditTodo={setEditTodo}
            fetchData={fetchData}
          />
        </div>
      </div>
    </div>
  );
}

export default App;
