import axios from "axios";
import { useEffect, useState } from "react";
import Map from "./Map";

const TodosList = ({
  key,
  todos,
  setTodos,
  editTodo,
  setEditTodo,
  editId,
  setEditId,
  fetchData
}) => {

  const [title, setTitle] = useState("");
  const [owner, setOwner] = useState("");
  const [location, setLocation] = useState("");

  // delete
  const updateDelete = (id)=> {
    deleteTask(id)
   }

  const deleteTask = (id) => {
    axios
      .delete("http://localhost:8000/todos/" + id)
      .then(function (response) {
        fetchData();
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  // edit
  const editHandler = (data) => {

    setTodos(
      todos.map((todo)=>
        todo.id === data.id ? {...todo, edited: !todo.edited} :todo
      )
    )
    setEditTodo(data);
    setEditId(data.id);
  };

  //update
  const editTask = (id) => {
    axios
      .put("http://localhost:8000/todos/" + id, {
        id: id,
        title: title,
        owner: owner,
        location: location,
        completed: false,
        edited:false
      })
      .then(function (response) {
        fetchData();
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  useEffect(() => {
    if (editTodo) {
      setTitle(editTodo.title);
      setOwner(editTodo.owner);
      setLocation(editTodo.location);
    }
  }, [setTitle, setOwner, setLocation, editTodo]);



  const completedHandler = (todo) => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return { ...item, completed: !item.completed };
        }
        completedTask(item.id)
        return item;
      })
    );
  };

  const completedTask = (id) => {
    axios
      .put("http://localhost:8000/todos/" + id, {
        id: id,
        title: title,
        owner: owner,
        location: location,
        completed: true,
      })
      .then(function (response) {
        fetchData();
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  return (
    <div>
      <table>
        <tbody>
      {todos.map((todo) => (

            <tr className="list-item" key={key}>
              <Map completedHandler={completedHandler} editHandler={editHandler} editId/>
            </tr>
        ))}
        </tbody>
      </table>
    </div>
  );
};

export default TodosList;
