import axios from "axios";
import { useEffect, useState } from "react";

const TodosList = ({
  todos,
  setTodos,
  editTodo,
  setEditTodo,
  editId,
  setEditId,
  fetchData
}) => {

  const [title, setTitle] = useState("");
  const [owner, setOwner] = useState("");
  const [location, setLocation] = useState("");

  // delete
  const updateDelete = (id)=> {
    deleteTask(id)
   }

  const deleteTask = (id) => {
    axios
      .delete("http://localhost:8000/todos/" + id)
      .then(function (response) {
        fetchData();
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  // edit
  const editHandler = (data) => {

    setTodos(
      todos.map((todo)=>
        todo.id === data.id ? {...todo, edited: !todo.edited} :todo
      )
    )
    setEditTodo(data);
    setEditId(data.id);
  };

  //update
  const editTask = (id) => {
    axios
      .put("http://localhost:8000/todos/" + id, {
        id: id,
        title: title,
        owner: owner,
        location: location,
        completed: false,
        edited:false
      })
      .then(function (response) {
        fetchData();
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  useEffect(() => {
    if (editTodo) {
      setTitle(editTodo.title);
      setOwner(editTodo.owner);
      setLocation(editTodo.location);
    }
  }, [setTitle, setOwner, setLocation, editTodo]);

  const locationChange = (e) => {
    setLocation(e.target.value);
  };

  const titleChange = (e) => {
    setTitle(e.target.value);
  };

  const ownerChange = (e) => {
    setOwner(e.target.value);
  };

  const completedHandler = (todo) => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return { ...item, completed: !item.completed };
        }
        completedTask(item.id)
        return item;
      })
    );
  };

  const completedTask = (id) => {
    axios
      .put("http://localhost:8000/todos/" + id, {
        id: id,
        title: title,
        owner: owner,
        location: location,
        completed: true,
      })
      .then(function (response) {
        fetchData();
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  return (
    <div>
      {todos.map((todo) => (
        <div>
          <table>
            <tbody></tbody>
            <tr className="list-item" key={todos.id}>
              <td
                className={`list ${todo.completed ? "complete" : ""}`}
                onChange={(e) => e.preventDefault()}
              >
                {todo.edited ? (
                  <input type="text" value={title} onChange={titleChange} />
                ) : (
                  todo.title
                )}{" "}
              </td>

              <td
                className={`list ${todo.completed ? "complete" : ""}`}
                onChange={(e) => e.preventDefault()}
              >
                {todo.edited ? (
                  <input type="text" value={owner} onChange={ownerChange} />
                ) : (
                  todo.owner
                )}{" "}
              </td>

              <td
                className={`list ${todo.completed ? "complete" : ""}`}
                onChange={(e) => e.preventDefault()}
              >
                {todo.edited ? (
                  <input
                    type="text"
                    value={location}
                    onChange={locationChange}
                  />
                ) : (
                  todo.location
                )}
              </td>

              <button
                className="button-complete task-button"
                onClick={() => completedHandler(todo)}
              >
                <span>
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-check-circle"
                    viewBox="0 0 16 16"
                  >
                    <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                    <path d="m10.97 4.97-.02.022-3.473 4.425-2.093-2.094a.75.75 0 0 0-1.06 1.06L6.97 11.03a.75.75 0 0 0 1.079-.02l3.992-4.99a.75.75 0 0 0-1.071-1.05" />
                  </svg>
                </span>
              </button>

              <button
                className="button-edit task-button"
                onClick={() => editHandler(todo)}>
                {todo.edited ? (
                  <span>
                  <button className="button-edit task-button save" onClick={() => editTask(editId)}>
                    <svg
                  xmlns="http://www.w3.org/2000/svg"
                  width="16"
                  height="16"
                  fill="currentColor"
                  class="bi bi-floppy"
                  viewBox="0 0 16 16"
                >
                  <path d="M11 2H9v3h2z" />
                  <path d="M1.5 0h11.586a1.5 1.5 0 0 1 1.06.44l1.415 1.414A1.5 1.5 0 0 1 16 2.914V14.5a1.5 1.5 0 0 1-1.5 1.5h-13A1.5 1.5 0 0 1 0 14.5v-13A1.5 1.5 0 0 1 1.5 0M1 1.5v13a.5.5 0 0 0 .5.5H2v-4.5A1.5 1.5 0 0 1 3.5 9h9a1.5 1.5 0 0 1 1.5 1.5V15h.5a.5.5 0 0 0 .5-.5V2.914a.5.5 0 0 0-.146-.353l-1.415-1.415A.5.5 0 0 0 13.086 1H13v4.5A1.5 1.5 0 0 1 11.5 7h-7A1.5 1.5 0 0 1 3 5.5V1H1.5a.5.5 0 0 0-.5.5m3 4a.5.5 0 0 0 .5.5h7a.5.5 0 0 0 .5-.5V1H4zM3 15h10v-4.5a.5.5 0 0 0-.5-.5h-9a.5.5 0 0 0-.5.5z" />
                    </svg>
                </button>
                </span>

                ) : (
                  <span>
                    <svg
                      xmlns="http://www.w3.org/2000/svg"
                      width="16"
                      height="16"
                      fill="currentColor"
                      className="bi bi-pencil"
                      viewBox="0 0 16 16"
                    >
                      <path d="M12.146.146a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1 0 .708l-10 10a.5.5 0 0 1-.168.11l-5 2a.5.5 0 0 1-.65-.65l2-5a.5.5 0 0 1 .11-.168zM11.207 2.5 13.5 4.793 14.793 3.5 12.5 1.207zm1.586 3L10.5 3.207 4 9.707V10h.5a.5.5 0 0 1 .5.5v.5h.5a.5.5 0 0 1 .5.5v.5h.293zm-9.761 5.175-.106.106-1.528 3.821 3.821-1.528.106-.106A.5.5 0 0 1 5 12.5V12h-.5a.5.5 0 0 1-.5-.5V11h-.5a.5.5 0 0 1-.468-.325" />
                    </svg>
                  </span>
                )}
              </button>

              <button
                className="button-delete task-button"
                onClick={() => updateDelete(todo.id)}
              >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-x-circle"
                    viewBox="0 0 16 16"
                  >
                    <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                    <path d="M4.646 4.646a.5.5 0 0 1 .708 0L8 7.293l2.646-2.647a.5.5 0 0 1 .708.708L8.707 8l2.647 2.646a.5.5 0 0 1-.708.708L8 8.707l-2.646 2.647a.5.5 0 0 1-.708-.708L7.293 8 4.646 5.354a.5.5 0 0 1 0-.708" />
                  </svg>
              </button>
            </tr>
          </table>
        </div>
      ))}
    </div>
  );
};

export default TodosList;
