import axios from "axios";
import { useState } from "react";

const Map = ({todo,fetchData, setTodos, editTodos, setEditTodo}) => {

  const [title, setTitle] = useState("");
  const [owner, setOwner] = useState("");
  const [location, setLocation] = useState("");


  // delete
  const updateDelete = (id)=> {
    deleteTask(id)
   }

  const deleteTask = (id) => {
    axios
      .delete("http://localhost:8000/todos/" + id)
      .then(function (response) {
        fetchData();
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  // edit
  const editHandler = (data) => {

    setTodos(
      todo.map((todo)=>
        todo.id === data.id ? {...todo, edited: !todo.edited} :todo
      )
    )
    setEditTodo(data);
    setEditId(data.id);
  };

  //update
  const editTask = (id) => {
    axios
      .put("http://localhost:8000/todos/" + id, {
        id: id,
        title: title,
        owner: owner,
        location: location,
        completed: false,
        edited:false
      })
      .then(function (response) {
        fetchData();
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  useEffect(() => {
    if (editTodo) {
      setTitle(editTodo.title);
      setOwner(editTodo.owner);
      setLocation(editTodo.location);
    }
  }, [setTitle, setOwner, setLocation, editTodo]);



  const completedHandler = (todo) => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return { ...item, completed: !item.completed };
        }
        completedTask(item.id)
        return item;
      })
    );
  };

  const completedTask = (id) => {
    axios
      .put("http://localhost:8000/todos/" + id, {
        id: id,
        title: title,
        owner: owner,
        location: location,
        completed: true,
      })
      .then(function (response) {
        fetchData();
        console.log(response);
      })
      .catch(function (error) {
        console.log(error);
      });
  };

  const locationChange = (e) => {
    setLocation(e.target.value);
  };

  const titleChange = (e) => {
    setTitle(e.target.value);
  };

  const ownerChange = (e) => {
    setOwner(e.target.value);
  };

  return (
    <tr className="list-item">
        <td
                // className={`list ${todo.completed ? "complete" : ""}`}
                onChange={(e) => e.preventDefault()}
               >
                {todo.edited ? (
                  <input type="text" value={title} onChange={titleChange} />
                ) : (
                  todo.title
                )}{" "}
        </td>

        <td
                // className={`list ${todo.completed ? "complete" : ""}`}
                onChange={(e) => e.preventDefault()}
              >
                {todo.edited ? (
                  <input type="text" value={owner} onChange={ownerChange} />
                ) : (
                  todo.owner
                )}{" "}
        </td>

        <td
                // className={`list ${todo.completed ? "complete" : ""}`}
                onChange={(e) => e.preventDefault()}
              >
                {todo.edited ? (
                  <input
                    type="text"
                    value={location}
                    onChange={locationChange}
                  />
                ) : (
                  todo.location
                )}
        </td>

        <td
                className="button-complete task-button"
                onClick={() => completedHandler(todo)}
              >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="16"
                    height="16"
                    fill="currentColor"
                    className="bi bi-check-circle"
                    viewBox="0 0 16 16"
                  >
                    <path d="M8 15A7 7 0 1 1 8 1a7 7 0 0 1 0 14m0 1A8 8 0 1 0 8 0a8 8 0 0 0 0 16" />
                    <path d="m10.97 4.97-.02.022-3.473 4.425-2.093-2.094a.75.75 0 0 0-1.06 1.06L6.97 11.03a.75.75 0 0 0 1.079-.02l3.992-4.99a.75.75 0 0 0-1.071-1.05" />
                  </svg>
        </td>

        <td
