import { useState } from "react";
import axios from "axios";
import { v4 as uuidv4 } from 'uuid';

const EditForm = ({editTodo, editId, fetchData }) => {
  const [inputTitle, setInputTitle] = useState("");
  const [inputOwner, setInputOwner] = useState("");
  const [inputLocation, setInputLocation] = useState("");

  const deleteTask = (id) => {
    axios.delete("http://localhost:8000/todos/" + id)
      .then(function(response) {
        fetchData();
        console.log(response)
      }).catch(function(error) {
        console.log(error)
      })
  }

  // const editHandler = (id) => {
  //   setTodos(
  //     todos.map((todo) =>
  //       todo.id === id ? { ...todo, edited: !todo.edited } : todo
  //     )
  //   );
  //   const findTodo = todos.find((todo) => todo.id === id);
  //   setEditTodo(findTodo);
  //   setEditId(findTodo.id);
  // };

  const locationChange = (e) => {
    setInputLocation(e.target.value);
  };

  const titleChange = (e) => {
    setInputTitle(e.target.value);
  };

  const ownerChange = (e) => {
    setInputOwner(e.target.value);
  };

  const completedHandler = (todo) => {
    setTodos(
      todos.map((item) => {
        if (item.id === todo.id) {
          return { ...item, completed: !item.completed };
        }
        return item;
      })
    );
  };

  // useEffect(() => {
  //   if (editTodo) {
  //     setInputTitle(editTodo.title);
  //     setInputOwner(editTodo.owner);
  //     setInputLocation(editTodo.location);

  //   } else {
  //     setInputTitle("");
  //   }
  // }, [setInputTitle,setInputOwner,setInputLocation, editTodo]);


  const onFormSubmit = (e) => {
    e.preventDefault();
    if (!editTodo) {
      addNewTask()
      setInputTitle("");
      setInputOwner("");
      setInputLocation("");
    } else {
      editTask(editId)
    }
     setInputTitle("");
     setInputOwner("");
     setInputLocation("");
  };

  const addNewTask = () => {
    axios.post('http://localhost:8000/todos' , {
    id: uuidv4(),
    title: inputTitle,
    owner:inputOwner,
    location:inputLocation,
    completed: false,
    edited:false
  })
  .then(function (response) {
    fetchData()
    console.log(response);
  })
  .catch(function (error) {
    console.log(error);
  });
  };

  const editTask = (id) => {
    axios.put("http://localhost:8000/todos/" + id , {
      id:id,
      title:inputTitle,
      owner:inputOwner,
      location:inputLocation,
      completed: false
    })
      .then(function() {
        fetchData()
      }).catch(function(error) {
        console.log(error)
      })
  }

  return (
    <form onSubmit={onFormSubmit}>
      <input
        type="text"
        placeholder="add a new task..."
        className="task-input"
        value={inputTitle}
        required
        onChange={titleChange}
      />
      <input
        type="text"
        placeholder="add an owner..."
        className="task-input"
        value={inputOwner}
        required
        onChange={ownerChange}
      />

       <input
        type="text"
        placeholder="add a location..."
        className="task-input"
        value={inputLocation}
        required
        onChange={locationChange}
      />

      <button type="submit" className="button-add">
      Add
      </button>
    </form>
  );
}

export default EditForm
