import { ErrorMessage, Form, Formik } from "formik";
import * as Yup from "yup";

const InputsForm = () => {
 const formFields = {
    title: "",
    owner: "",
    location: ""
 }

 const submitHandler = values => {console.log(values)}

 const valicationSchema = Yup.object ({
    title:Yup.string().required("enter a title!") ,
    owner:Yup.string().required("enter an owner!") ,
    location:Yup.string().required("enter a location!") 
 })

  return (
    <div>

<Formik validateOnBlur={false} validateOnChange={false} onSubmit={submitHandler} initialValues={formFields} validactionSchema={valicationSchema}>
<Form>

<label>name</label>
<field type="text" name="name"/>
<ErrorMessage name="name"/>

<label>owner</label>
<field type="text" name="owner"/>
<ErrorMessage name="name"/>

<label>location</label>
<field type="text" name="location"/>
<ErrorMessage name="name"/>

</Form>
</Formik>
    </div>
  )
}

export default InputsForm
