import { useEffect } from "react";

const Form = ({ input, setInput, todos, setTodos, editTodo, setEditTodo,addNewTask,editId,setEditId,editTask }) => {
//   const updateTodo = (title, id, completed) => {
//     const copyList = [...todos];
//     const itemToEdit = copyList.find(item => item.id === id);
//     itemToEdit.title = input;
//     setTodos(copyList);
//     setEditTodo("");
// }

  useEffect(() => {
    if (editTodo) {
      setInput(editTodo.title);
      // setTodos(editTodo.title)
    } else {
      setInput("");
    }
  }, [setInput, editTodo]);

  const onInputChange = (e) => {
    setInput(e.target.value);
  };

  const onFormSubmit = (e) => {
    e.preventDefault();
    if (!editTodo) {
      addNewTask()
      setInput("");
    } else {
      editTask(editId)

    }
    setInput("");

  };
  return (
    <form onSubmit={onFormSubmit}>
      <input
        type="text"
        placeholder="add a new task"
        className="task-input"
        value={input}
        required
        onChange={onInputChange}
      />
      <button type="submit" className="button-add">
        {editTodo ? "edit" : "add"}
      </button>
    </form>
  );
};

export default Form;
